CREATE TABLE "public"."users" ("id" serial NOT NULL, "uuid" uuid NOT NULL, "slug" text NOT NULL, "first_name" text NOT NULL, "last_name" text NOT NULL, "password" text NOT NULL, "email" text NOT NULL, "address_id" integer, "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz, "updated_by" integer, "deleted_at" timestamptz, "deleted_by" integer, "status" text NOT NULL, PRIMARY KEY ("id") , UNIQUE ("id"), UNIQUE ("uuid"), UNIQUE ("slug"));
